/**
 * Vlossom Brand Logo Component
 *
 * Official brand logos from /design/brand/logos
 * Supports both icon and wordmark variants in purple and cream colors.
 *
 * Usage:
 * - <VlossomIcon /> - Icon only (32x32 default)
 * - <VlossomWordmark /> - Full wordmark
 * - <VlossomLogo /> - Icon + wordmark combo
 */

import { cn } from "@/lib/utils";

interface LogoProps {
  className?: string;
  size?: number;
  height?: number; // For wordmark - if provided, calculates width from aspect ratio
  variant?: "purple" | "cream" | "auto"; // auto = purple on light, cream on dark
}

