/**
 * Combined Vlossom Logo - Icon + Wordmark
 */
export function VlossomLogo({
  className,
  iconSize = 32,
  wordmarkSize,
  wordmarkHeight,
  variant = "purple",
}: LogoProps & { iconSize?: number; wordmarkSize?: number; wordmarkHeight?: number }) {
  return (
    <div className={cn("flex items-center gap-2", className)}>
      <VlossomIcon size={iconSize} variant={variant} />
      <VlossomWordmark
        size={wordmarkSize}
        height={wordmarkHeight}
        variant={variant}
      />
    </div>
  );
}

export default VlossomLogo;
